import React, { useState } from 'react';

function CardAtleta({ atleta, onAddToFavorites }) {
  const [isFavorite, setIsFavorite] = useState(false);

  const handleAddToFavorites = () => {
    setIsFavorite(true);
    onAddToFavorites(atleta);
  };

  return (
    <div className="card">
      <h1>{atleta.player_name}</h1>
      <img src={atleta.player_image} alt={atleta.player_name} />
      <p>
        Idade: {atleta.player_age} | Posição: {atleta.player_type} | Time: {atleta.team_name}
      </p>
      {!isFavorite && (
        <button onClick={handleAddToFavorites}>Adicionar aos favoritos</button>
      )}
      {isFavorite && <p>Já nos favoritos</p>}
    </div>
  );
}

export default CardAtleta;
