import React from 'react';

function CardAtleta({ atleta, onAddToFavorites, favoritos }) {
  const isFavorito = favoritos.some((favorito) => favorito.player_id === atleta.player_id);

  const handleAddToFavorites = () => {
    onAddToFavorites(atleta);
  };

  return (
    <div className="card">
      <h1>{atleta.player_name}</h1>
      <img src={atleta.player_image} alt={atleta.player_name} />
      <p>
        Idade: {atleta.player_age} | País: {atleta.player_country} | Posição: {atleta.player_type} | Time: {atleta.team_name}
      </p>
      {isFavorito ? (
        <p>Já está nos favoritos</p>
      ) : (
        <button onClick={handleAddToFavorites}>Adicionar aos favoritos</button>
      )}
    </div>
  );
}

export default CardAtleta;
