      .catch((error) => {
        console.error('Erro ao buscar atletas:', error);
        setResultadosSelecao([]);
        setResultadosTime([]);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    // Você pode adicionar lógica aqui para carregar atletas iniciais, se necessário
  }, []);

  return (
    <div>
      <h1>Pesquisa de Atletas</h1>
      <input
        type="text"
        placeholder="Digite o nome do atleta"
        value={nomeAtleta}
        onChange={handleInputChange}
      />
      <button onClick={buscarAtletas}>Pesquisar</button>
      {isLoading && <p>Carregando...</p>}
      <h2>Seleção</h2>
      <div className="resultado">
        {resultadosSelecao.map((atleta, index) => (
          <CardAtleta
            key={index}
            atleta={atleta}
            onAddToFavorites={addToFavorites}
            favoritos={favoritos}
          />
        ))}
      </div>
      <h2>Time</h2>
      <div className="resultado">
        {resultadosTime.map((atleta, index) => (
          <CardAtleta
            key={index}
            atleta={atleta}
            onAddToFavorites={addToFavorites}
            favoritos={favoritos}
          />
        ))}
      </div>
      <PainelFavoritos favoritos={favoritos} />
    </div>
  );
}

export default PesquisarAtletas;
