import React from 'react';

function CardAtleta({ atleta, onAddToFavorites, selectedPlayer, setSelectedPlayer }) {
  const handleAddToFavorites = () => {
    onAddToFavorites(atleta);
  };

  const handlePlayerClick = () => {
    setSelectedPlayer(atleta);
  };

  const isSelected = selectedPlayer && selectedPlayer.player_id === atleta.player_id;

  return (
    <div className={`card ${isSelected ? 'selected' : ''}`} onClick={handlePlayerClick}>
      <h1>{atleta.player_name}</h1>
      <img src={atleta.player_image} alt={atleta.player_name} />
      <p>
        Idade: {atleta.player_age} | País: {atleta.player_country} | Posição: {atleta.player_type} | Time: {atleta.team_name}
      </p>
      {isSelected ? (
        <p>Selecionado</p>
      ) : (
        <button onClick={handleAddToFavorites}>Adicionar aos favoritos</button>
      )}
    </div>
  );
}

export default CardAtleta;
