import React, { useState } from 'react';

function PesquisaJogador({ onAddToFavorites }) {
  const [playerName, setPlayerName] = useState('');
  const [playerData, setPlayerData] = useState(null);

  const apiKey = 'COLOQUE_AQUI_SUA_CHAVE';

  const handleSearch = async (e) => {
    e.preventDefault();

    const playersUrl = `https://apiv3.apifootball.com/?action=get_players&player_name=${playerName}&APIkey=${apiKey}`;

    try {
      const response = await fetch(playersUrl);
      const data = await response.json();
      setPlayerData(data);
    } catch (error) {
      console.error('Erro ao obter os detalhes do jogador:', error);
    }
  };

  const handleAddToFavorites = () => {
    if (playerData) {
      onAddToFavorites(playerData);
    }
  };

  return (
    <div>
      <h1>Buscar Jogador</h1>
      <form onSubmit={handleSearch}>
        <label htmlFor="playerName">Nome do Jogador:</label>
        <input
          type="text"
          id="playerName"
          name="playerName"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          required
        />
        <button type="submit">Pesquisar</button>
      </form>
      {playerData && (
        <div>
          <pre>{JSON.stringify(playerData, null, 2)}</pre>
          <button onClick={handleAddToFavorites}>Adicionar aos favoritos</button>
        </div>
      )}
    </div>
  );
}

export default PesquisaJogador;
