import React, { useState } from 'react';
import axios from 'axios';
import CardAtleta from './CardAtleta';

function PesquisaAtletas() {
  const [nomeAtleta, setNomeAtleta] = useState('');
  const [resultados, setResultados] = useState([]);
  const [favoritos, setFavoritos] = useState([]);

  const handleNomeAtletaChange = (event) => {
    setNomeAtleta(event.target.value);
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://apiv3.apifootball.com/?action=get_players&player_name=${nomeAtleta}&APIkey=23a2495a633253850a9ad8999dd626eb7639663230f50f483ed40b33ea5ede3c`);
      setResultados(response.data);
    } catch (error) {
      console.error('Erro na pesquisa:', error);
    }
  };

  const handleAddToFavorites = (atleta) => {
    setFavoritos([...favoritos, atleta]);
  };

  return (
    <div>
      <h1>Pesquisa de Atletas</h1>
      <div>
        <input
          type="text"
          placeholder="Digite o nome do atleta"
          value={nomeAtleta}
          onChange={handleNomeAtletaChange}
        />
        <button onClick={handleSearch}>Pesquisar</button>
      </div>
      <div className="resultados">
        {resultados.map((atleta, index) => (
          <CardAtleta
            key={index}
            atleta={atleta}
            onAddToFavorites={handleAddToFavorites}
          />
        ))}
      </div>
    </div>
  );
}

export default PesquisaAtletas;
